import profilePhoto from '../images/Avatar.png'

function Main(props){
    function handleEditAvatarClick(){
        const popupAvatar = document.querySelector('#putchAvatar');
        popupAvatar.classList.add('popup_opened')
    }

    function handleEditProfileClick(){
        const popupEdit = document.querySelector('#popupEdit');
        popupEdit.classList.add('popup_opened')
    }

    function handleAddPlaceClick(){
        const popupAdd = document.querySelector('#addPopup');
        popupAdd.classList.add('popup_opened')
    }

    return(
        <main>
        <section className="profile">
          <button className="profile__button-avatar" type="button" onClick={handleEditAvatarClick}>
            <img src={profilePhoto} alt="Аватар" className="profile__avatar" />
          </button>
          <div className="profile__info">
            <h1 className="profile__name">Жак-Ив Кусто</h1>
            <button className="profile__edit-button" type="button" onClick={handleEditProfileClick}></button>
            <p className="profile__occupation">Исследователь океана</p>
          </div>
          <button className="profile__add-button" type="button" onClick={handleAddPlaceClick}></button>
        </section>
        <section className="grid">
        </section>
      </main>
    )
}

export default Main;
