import profilePhoto from '../images/Avatar.png'
import React from "react";
import {api} from '../utils/Api'

function Main(props){
    const [userName, setUserName] = React.useState([]);
    const [userDescription, setUserDescription] = React.useState([]);
    const [userAvatar, setUserAvatar] = React.useState([]);
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.getUserInfo()
         .then((data) =>{
            //console.log(data)
            setUserName(data.name);
            setUserDescription(data.about);
            setUserAvatar(data.avatar);
         })
          .catch((err)=>{
            console.log(`Ошибка. Запрос не выполнен ${err}`);
          })
      }, []);

      React.useEffect(() => {
        api.getInitialCards()
         .then((data) =>{
            setCards(data)
            cards.forEach((item) => {
                console.log(item)
            })
            console.log(cards);
         })
          .catch((err)=>{
            console.log(`Ошибка. Запрос не выполнен ${err}`);
          })
      }, []);

    return(
        <main>
        <section className="profile">
          <button className="profile__button-avatar" type="button" onClick={props.handleEditAvatarClick} >
            <img src={userAvatar} alt="Аватар" className="profile__avatar" />
          </button>
          <div className="profile__info">
            <h1 className="profile__name">{userName}</h1>
            <button className="profile__edit-button" type="button" onClick={props.handleEditProfileClick}></button>
            <p className="profile__occupation">{userDescription}</p>
          </div>
          <button className="profile__add-button" type="button" onClick={props.handleAddPlaceClick}></button>
        </section>
        <section className="grid">
            {cards.forEach((item) =>{
                console.log(item);
                <div className="grid__item">
                <button type="button" className="grid__trash"></button>
                <img src="./images/dombay.png" alt="Домбай" className="grid__image" />
                <div className="grid__box">
                  <h2 className="grid__title">Домбай</h2>
                  <div className="grid__contaner">
                    <button type="button" className="grid__like"></button>
                    <p className="grid__like-count">0</p>
                  </div>
                </div>
              </div>
            })}
        </section>
      </main>
    )
}

export default Main;
