//import profilePhoto from '../images/Avatar.png'
import React from "react";
import { api } from '../utils/Api'
import Card from './Card';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Main(props) {
    const [cards, setCards] = React.useState([]);
    const userInfo = React.useContext(CurrentUserContext);

    React.useEffect(() => {
        api.getInitialCards()
            .then((data) => {
                setCards(data)
            })
            .catch((err) => {
                console.log(`Ошибка. Запрос не выполнен ${err}`);
            })
    }, []);

    return (
        <main>
            <section className="profile">
                <button className="profile__button-avatar" type="button" onClick={props.handleEditAvatarClick} >
                    <img src={userInfo.avatar} alt="Аватар" className="profile__avatar" />
                </button>
                <div className="profile__info">
                    <h1 className="profile__name">{userInfo.name}</h1>
                    <button className="profile__edit-button" type="button" onClick={props.handleEditProfileClick}></button>
                    <p className="profile__occupation">{userInfo.about}</p>
                </div>
                <button className="profile__add-button" type="button" onClick={props.handleAddPlaceClick}></button>
            </section>
            <section className="grid">
                {
                    cards.map((item) => {
                        //console.log(item.likes.length)
                        //console.log(item)
                        return (
                            <Card
                                link={item.link}
                                key={item._id}
                                name={item.name}
                                likesCount={item.likes.length}
                                onCardClick={props.onCardClick}
                                item={item}
                            />
                        );
                    })

                }
            </section>
        </main>
    )
}

export default Main;
